// Scan applies the selector query and scans the result into the given value.
func (ods *OrderDeliverySelect) Scan(ctx context.Context, v interface{}) error {
	if err := ods.prepareQuery(ctx); err != nil {
		return err
	}
	ods.sql = ods.OrderDeliveryQuery.sqlQuery(ctx)
	return ods.sqlScan(ctx, v)
}

// ScanX is like Scan, but panics if an error occurs.
func (ods *OrderDeliverySelect) ScanX(ctx context.Context, v interface{}) {
	if err := ods.Scan(ctx, v); err != nil {
		panic(err)
	}
}

